function mdx_logo_sec(selectV){
    if(selectV == '1'){
        jQuery('.logo_logo,.logo_text').hide();
    }else if(selectV == '2'){
        jQuery('.logo_text').hide();
        jQuery('.logo_logo').show();
    }else if(selectV == '3'){
        jQuery('.logo_text').show();
        jQuery('.logo_logo').hide();
    }
}
